import React, { useState } from 'react';

// e.target.value,
// incrementWordIndex,
// currentWord,
// setCorrect,
// handleLetterAccuracy,

const checkSpace = (
  userWord,
  incrementWordIndex,
  currentWord,
  setCorrect,
  handleLetterAccuracy,
) => {
  // hit space on empty word
  if (userWord[0] === ' ') {
    return '';
  }
  // handle next word, check for correctness
  if (userWord[userWord.length - 1] === ' ') {
    incrementWordIndex();
    console.log(userWord +'and' +currentWord);
    setCorrect(userWord.slice(0,userWord.length) === currentWord);
    return '';
  }
  // if the letter was correect
  if (userWord[userWord.length - 1] === currentWord[userWord.length - 1]) {
    return userWord;
  }
  // if the letter was wrong
  if (userWord[userWord.length - 1] !== currentWord[userWord.length - 1]) {
    handleLetterAccuracy();
    return userWord;
  }
  return userWord;
};

const InputBar = (props) => {
  const {
    currentWord,
    incrementWordIndex,
    handleLetterAccuracy,
    wasCorrect,
    setCorrect,
  } = props;
  const [userWord, setUserWord] = useState('');

  const letterIndex = userWord.length;
  return (
    <>
      <p>
        letter index
        {` ${letterIndex}`}
      </p>
      <p>
        the current is
        {` ${currentWord}`}
      </p>
      <input
        onChange={e => setUserWord(checkSpace(
          e.target.value,
          incrementWordIndex,
          currentWord,
          setCorrect,
          handleLetterAccuracy,
        ))}
        value={userWord}
      />
      <p>
        {wasCorrect && 'correct'}
      </p>
    </>
  );
};

export default InputBar;
